<?phpnamespace App\Http\Controllers\Dashboard;use App\Category;use App\User;use Illuminate\Http\Request;use App\Http\Controllers\Controller;use Illuminate\Support\Facades\Storage;use Illuminate\Validation\Rule;use Intervention\Image\Facades\Image;class CategoryController extends Controller{		public function index(Request $request)    {       $categories= Category::when($request->search,function ($q) use ($request){       		return $q->whereTranslationLike('name','%'.$request->search.'%');			 })->latest()->paginate(5);       return view('dashboard.categories.index',compact('categories'));    }//end of index fun    public function create()    {        return view('dashboard.categories.create');    }//end of create fun    public function store(Request $request)    {    		$rules=[];    		foreach (config('translatable.locales') as $local)    		{    				$rules +=[$local.'.name'=>['required',Rule::unique('category_translations','name')]];				}    		$request->validate($rules    			/*	[    				'ar.*'=>'required|unique:category_translations,name',						'en.*'=>'required|unique:category_translations,name',			    	]*/				);    		Category::create($request->all());				session()->flash('success', __('site.added-successfully'));    		return redirect()->route('dashboard.categories.index');    }//enf of store    public function edit(Category $category)    {       return view('dashboard.categories.edit',compact('category'));    }//enf of edit    public function update(Request $request, Category $category)		{				$rules=[];				foreach (config('translatable.locales') as $local){						$rules +=[$local.'.name'=>['required',								Rule::unique('category_translations','name')->ignore($category->id,'category_id')]];				}				$request->validate($rules					  // [ 'name'=>['required',Rule::unique('categories')->ignore($category->id)],						//'name' => 'required|unique:categories,name,'.$category->id,طريقه قديمه]				);				$category->update($request->all());				session()->flash('success', __('site.updated-successfully'));				return redirect()->route('dashboard.categories.index');		}//enf of update    public function destroy(Category $category)    {      $category->delete();				session()->flash('success', __('site.deleted-successfully'));				return redirect()->route('dashboard.categories.index');    }//enf of destroy}